import React, { Component } from 'react';
import NavigationURL from './../router/NavigationURL'
import {BrowserRouter as Router, Switch, Route, Link} from "react-router-dom";

class NewsItem extends Component {
    constructor(props) {
        super(props)
    }
   
    render() {
        return (
            <div className="col-4 grid">
            <div className="card-deck">
              <div className="card">
                <Link to ={"/details/" + this.props.title + "." + this.props.NewsID + ".html"}><img className="card-img-top" src={this.props.pic} alt="" /></Link>
                <div className="card-body">
                  <h4 className="card-title text-center">{this.props.title}</h4>
                  <p className="card-text text-center">{this.props.quote}</p>
                </div>
              </div>
            </div>
            <hr />
          </div>
        );
    }
}

export default NewsItem;
